package errors

// ConnectionError message to indicate that the communication with an external entity has failed.
const ConnectionError = "HTTP connection error"

// MarshalError message to indicate errors with the json.Marshal operation.
const MarshalError = "cannot marshal structure"

// OpeningFileError message to indicate errors when opening a file.
const OpeningFileError = "cannot open the file"

// IOError during object reading.
const IOError = "I/O error"